import React, {useEffect, useState} from 'react'
import sass from './registrationPage.module.sass'
import Form from './form'
import {connect} from 'react-redux'
import {apiRequest} from '../../API/api'
import {setTokenThunk} from '../../Redux/regReducer'
import {setUpdatedUsersThunk} from '../../Redux/usersReducer'

const RegistrationPage = (props) => {

    useEffect( () => {
        props.setTokenThunk()
    }, [])

    let [photo, addPhoto] = useState (null)

    const onPhotoAdded = (event) => {
        addPhoto(event.target.files[0])
    }

    const onSubmit = async(formData) => {
        console.log(photo)
        await (apiRequest.register(formData.name, formData.email, formData.phone,
            Number(formData.position_id), photo, props.token) )
        props.setUpdatedUsersThunk()
    }

    return (
        <div id='registrationPage' className={sass.registrationPage}>
            <h1>Register to get a work</h1>
            <p className={sass.regSubtitle}>Attention! After successful registration and alert,
                update the list of users in the block from the top</p>
            <Form onSubmit={onSubmit} onPhotoAdded={onPhotoAdded}/>
            <div className={sass.modal}>
                <div className={sass.modalInner}>
                    <div>
                        <h2>Congratulations!</h2>
                        <img src="" alt=""/>
                    </div>
                    <div>You have successfully</div>
                    <div>
                        <button></button>
                    </div>
                </div>
            </div>
        </div>
    )
}

const mapStateToProps = (state) => {
    return { token: state.regData.token }
}

const RegistrationPageContainer = connect (mapStateToProps, {setTokenThunk, setUpdatedUsersThunk})(RegistrationPage)

export default RegistrationPageContainer
