import {apiRequest} from '../API/api'

const initialState = {
    users: []
}

export const usersReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'SET_USERS':
            return {
                ...state,
                users: action.payload,
            }
        default:
            return state
    }
}

const setUsersAC = (payload) => ({type: 'SET_USERS', payload})

/*const apiRequest = {
    getUsers() {
        return axios.get(`https://frontend-test-assignment-api.abz.agency/api/v1/users?page=1&count=6`)
            .then (response => response.data)
    }
}*/

export const setUsersThunk = () => {
    return (dispatch) => {
        apiRequest.getUsers().then (data => {
            console.log(data.data.users)
            dispatch (setUsersAC(data.data.users) )
        })
    }
}
