import React from 'react'

export let Input = ({input, meta, ...props}) => {
    let anError = meta.error && meta.touched;
    return(
        <div className= { 'field' + ' ' + (anError ? 'error' : ' ')}>
            <input {...input} {...props} />
            {anError && <p>'Error'</p>}
        </div>
    )
}
