import React from 'react'
import {Field, reduxForm} from 'redux-form'

const Form = (props) => {
    return (
        <form>
            <p>Name</p>
            <Field component={'input'} name={'userName'} placeholder={'Your name'}/>
            <p>Email</p>
            <Field component={'input'} name={'email'} placeholder={'Your email'}/>
            <p>Phone number</p>
            <Field component={'input'} name={'phoneNumber'}  placeholder={'+380 XX XXX XX XX'}/>
            <h6>Enter phone number in open format</h6>

            <button>

            </button>
        </form>
    )
}

const RegistrationForm = (props) => {
    return (
        <div >
            <h2>Register to get a work</h2>
            <h4>Attention! After successful registration and alert,
                update the list of users in the block from the top</h4>
            <ReduxRegistrationForm />
        </div>
    )
}

const ReduxRegistrationForm = reduxForm ({form: 'registration'}) (Form)

export default RegistrationForm
