import * as axios from 'axios'

export const apiRequest = {
    getUsers() {
        return axios.get(`https://frontend-test-assignment-api.abz.agency/api/v1/users?page=1&count=6`)
    },
    getToken() {
        return axios.get(`https://frontend-test-assignment-api.abz.agency/api/v1/token`)
    },
    register(name, email, phone, position_id, file, token) {
        var formData = new FormData();
// file from input type='file'
        var fileField = document.querySelector('input[type="file"]');
        formData.append('position_id', position_id);
        formData.append('name', name);
        formData.append('email', email);
        formData.append('phone', phone);
        formData.append('photo', file);
        return axios.post(`https://frontend-test-assignment-api.abz.agency/api/v1/users`,
            {name, email, phone, position_id, photo: file}, {headers: {'token': token, }})
    },
}
